$options = "";

foreach($this->model_catalog_product->getProductOptions($result['product_id']) as $option){
		if($option['type'] != 'image' ) {
			$options .= $option['name'].": ";
			foreach($option['option_value'] as $option_value){
				$options .= $option_value['name']." ";
			}
			$options .= "<br/><br/>";
		}
}


$results_img = $this->model_catalog_product->getProductImages($result['product_id']);
$additional_img = array();
foreach ($results_img as $result_img) {
	if ($result_img['image']) {
		$image_additional = $this->model_tool_image->resize($result_img['image'], $this->config->get('config_image_product_width'), $this->config->get('config_image_product_height'));
	} else {
		$image_additional = false;
	}
	$additional_img[] = $image_additional;
}

