			//var html = '';
			var additional_info = $(element).find('.additional_info').html();
			if (additional_info != null) {
				html += '<div class="additional_info">' + additional_info + '</div>';
			}

			var additional_img = $(element).find('.additional_img').html();
			if (additional_img != null) {
				html += '<div class="additional_img">' + additional_img + '</div>';
			}

			$(function () {
				$(".image").each(function (indx, el) {
					var image = $(".img", el),
					next = $(el).parent();
					$(".hover", next).hover(function () {
						var newsrc = $(this).attr("rel");
						image.attr({
							src: newsrc
						})
					})
				})
			});

			(function($){
				$(document).ready(function(){
					$(".additional_img").mCustomScrollbar({
						theme:"dark-thin"
					});
				});
			})(jQuery);

